sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const {
        query
    } = event.payload.params;

    const {state,setState} = api;
    // debugger;
    // console.log(query.substring(query.lastIndexOf('=') + 1).replace('"'));
    setState('filterTitle', query.substring(query.lastIndexOf('=') + 1).replace('"'));
   // event.payload.title = query.substring(query.lastIndexOf('=') + 1).replace('"');

}
